loginUrl:
//获取繁简转换设置
var v=source.getVariable();
//判断设置是否为空
if(v==null||v==""){
	v=0;
	source.setVariable(v);
	java.toast("未设置繁简转换，默认关闭。");
	}else{v=v};
//保存设置
function set(num){
	msg=num==1||0?
	"已开启繁简转换！":"已关闭繁简转换！";
	tips="\n刷新后生效。"
	java.longToast("设置成功：\n"+
		msg+tips);
	return source.setVariable(num);
	}
//获取设置
function get(v){
	msg=v==1||0?
	"繁简转换开启！":"繁简转换关闭！";
	java.longToast("当前状态：\n"+msg);
	return;
}
//按设置执行
var tzs=v==1?true:false;
tzs?java.t2s(result):result;

